import { useEffect, useRef } from "react";
import './App.css';
import Editor from '@monaco-editor/react';

function App() {
  const editorRef = useRef(null);
  const consolaRef = useRef(null);

  useEffect(() => {
    // Este efecto se ejecutará al montar el componente
    // Se podría utilizar para realizar alguna inicialización si es necesario
  }, []);

  function handleEditorDidMount(editor, id) {
    if (id === "editor") {
      editorRef.current = editor;
    } else if (id === "consola") {
      consolaRef.current = editor;
    }
  }

  function interpretar() {
    var entrada = editorRef.current.getValue();
    fetch('http://localhost:4000/interpretar', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ entrada: entrada }),
    })
      .then(response => response.json())
      .then(data => {
        consolaRef.current.setValue(data.Respuesta);
      })
      .catch((error) => {
        alert("Ya no sale comp1")
        console.error('Error:', error);
      });
  }

  const cargarArchivo = (event) => {
    var file = event.target.files[0];
    var reader = new FileReader();
    reader.onload = function (event) {
      var contents = event.target.result;
      editorRef.current.setValue(contents);
    };
    reader.readAsText(file);
  }

  return (
    <div className="App">
      <nav className="navbar">
        <h1 className="navbar-title">OLC PROYECTO 2</h1>
        <div className="navbar-buttons">
          <input type="file" id="file" className="btn" onChange={cargarArchivo} />
          <button className="btn" onClick={interpretar}>Ejecutar</button>
          <div className="dropdown">
            <button className="btn dropdown-btn">Reportes</button>
            <div className="dropdown-content">
              <a href="#">Opción 1</a>
              <a href="#">Opción 2</a>
              <a href="#">Opción 3</a>
            </div>
          </div>
        </div>
      </nav>

      <div className="text-center">
        <h1>CompiScript+</h1>
      </div>

      <div className="text-center">
        <div className="container">
          <div className="row">
            <div className="col">
              <p>Entrada</p>
              <Editor height="90vh" defaultLanguage="java" defaultValue="" theme="vs-dark" onMount={(editor) => handleEditorDidMount(editor, "editor")} />
            </div>
            <div className="col">
              <p>Consola</p>
              <Editor height="90vh" defaultLanguage="cpp" defaultValue="" theme="vs-dark" options={{ readOnly: true }} onMount={(editor) => handleEditorDidMount(editor, "consola")} />
            </div>
          </div>
        </div>
      </div>
    </div>
  );
}

export default App;
