import { useEffect, useState, useRef } from "react"
import './App.css';
import Editor from '@monaco-editor/react';

function App() {
  const editorRef = useRef(null);
  const consolaRef = useRef(null);

  function handleEditorDidMount(editor, id) {
    if (id === "editor") {
      editorRef.current = editor;
    } else if (id === "consola") {
      consolaRef.current = editor;
    }
  }


  function interpretar() {
    var entrada = editorRef.current.getValue();
    fetch('http://localhost:4000/interpretar', {
      method: 'POST',
      headers: {
        'Content-Type': 'application/json',
      },
      body: JSON.stringify({ entrada: entrada }),
    })
      .then(response => response.json())
      .then(data => {
        consolaRef.current.setValue(data.Respuesta);
      })
      .catch((error) => {
        alert("ERROR AL SOLICITAR PETICION")
        console.error('Error:', error);
      });
  }

  const CargarArchivo = (event) => {
    var file = event.target.files[0];
    var reader = new FileReader();
    reader.onload = function (event) {
      var contents = event.target.result;
      editorRef.current.setValue(contents);
    };
    reader.readAsText(file);
  }


  return (
    <div className="App">
  <div className="text-center">
    <h1>Prueba</h1>
  </div>
  <div className="text-center">
    <div className="container">
      <div className="row">
        <input type="file" id="file" className="form-control form-control-lg" onChange={CargarArchivo} />
        <input type="button" value="Interpretar" id="btnCargar" className="form-control form-control-lg" onClick={interpretar} />
        <div className="dropdown">
          <button className="btn btn-secondary dropdown-toggle" type="button" id="dropdownMenuButton" data-toggle="dropdown" aria-haspopup="true" aria-expanded="false">
            Reportes
          </button>
          <div className="dropdown-menu" aria-labelledby="dropdownMenuButton">
            <a className="dropdown-item" href="#">Tabla de errores</a>
            <a className="dropdown-item" href="#">Tabla de Símbolos</a>
            <a className="dropdown-item" href="#">Arbol AST</a>
            <a className="dropdown-item" href="#">Salidas en consola</a>
          </div>
        </div>
      </div>
    </div>
  </div>
  <br></br>
  <div className="text-center" style={{ height: "50%", width: "50%" }}>
    <div className="container" >
      <div className="row">
        <div className="col">
          <p>Entrada</p>
          <Editor height="90vh" defaultLanguage="java" defaultValue="" theme="vs-dark" onMount={(editor) => handleEditorDidMount(editor, "editor")} />
        </div>
        <div className="col">
          <p>Consola</p>
          <Editor height="90vh" defaultLanguage="cpp" defaultValue="" theme="vs-dark" options={{ readOnly: true }} onMount={(editor) => handleEditorDidMount(editor, "consola")} />
        </div>
      </div>
    </div>
  </div>
</div>

  );
}

export default App;
